  const [rewardTab, setRewardTab] = useState<'shop' | 'inventory'>('shop');

  // Load Games from API
  useEffect(() => {
    const fetchGames = async () => {
      try {
        const games = await api.games.list();
        setRequests(games);
      } catch (error) {
        console.error("Failed to load games", error);
      }
    };
    fetchGames();

    // Polling for new games (Real-time effect)
    const interval = setInterval(fetchGames, 5000);
    return () => clearInterval(interval);
  }, []);

  // MASA BAƒûLAMA FONKSƒ∞YONU
  const handleTableSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setMatchError('');
    setLoadingTable(true);

    setTimeout(() => {
      const formattedCode = tableCode.toUpperCase().replace(/\s/g, '');
      if (VALID_TABLES.includes(formattedCode)) {
        setIsMatched(true);
        setTableCode(formattedCode);
      } else {
        setMatchError('Ge√ßersiz masa kodu! (√ñrn: MASA01)');
      }
      setLoadingTable(false);
    }, 1000);
  };

  // OYUN KURMA FONKSƒ∞YONU
  const handleCreateGame = async (gameType: string, points: number) => {
    if (!isMatched) {
      alert("Oyun kurmak i√ßin √∂nce bir masaya baƒülanmalƒ±sƒ±n!");
      setIsCreateModalOpen(false);
      return;
    }

    try {
      const newGame = await api.games.create({
        hostName: currentUser.username,
        gameType,
        points,
        table: tableCode
      });

      setRequests([newGame, ...requests]);
      setIsCreateModalOpen(false);
      alert("Oyun Lobiye Eklendi! Rakip bekleniyor...");
    } catch (error) {
      alert("Oyun kurulurken hata olu≈ütu.");
    }
  };

  // OYUNA KATILMA FONKSƒ∞YONU
  const handleJoinGame = async (id: number) => {
    if (!isMatched) {
      alert("Oyuna katƒ±lmak i√ßin √∂nce bir masaya baƒülanmalƒ±sƒ±n!");
      return;
    }

    try {
      await api.games.join(id);
      setRequests(requests.filter(req => req.id !== id));
      alert("Oyun E≈üle≈ümesi Ba≈üarƒ±lƒ±! Oyun ekranƒ±na y√∂nlendiriliyorsunuz...");
    } catch (error) {
      alert("Oyuna katƒ±lƒ±rken hata olu≈ütu.");
    }
  };

  // PROFƒ∞L G√ñR√úNT√úLEME
  const handleViewProfile = (username: string) => {
     if (username === currentUser.username) {
         setProfileUser(currentUser);
     } else {
         // Not: Ger√ßek projede burada api.users.get(username) √ßaƒürƒ±lƒ±r
         setProfileUser({
             id: 999,
             username: username,
             email: 'player@cafe.com',
             points: Math.floor(Math.random() * 2000),
             wins: Math.floor(Math.random() * 50),
             gamesPlayed: Math.floor(Math.random() * 100)
         });
     }
     setIsProfileOpen(true);
  };

  // √ñD√úL ALMA FONKSƒ∞YONU
  const handleRedeemReward = (reward: Reward) => {
    if (currentUser.points < reward.cost) return;

    if (window.confirm(`${reward.title} √∂d√ºl√ºn√º ${reward.cost} puan kar≈üƒ±lƒ±ƒüƒ±nda almak istiyor musun?`)) {
        // Puan d√º≈ü
        const updatedUser = { ...currentUser, points: currentUser.points - reward.cost };
        onUpdateUser(updatedUser);

        // Kupon olu≈ütur
